ssList.remove('hidden');<?php overlay.classList.remove('active');<?php }<?php });<?php <?php document.getElementById('banner-upload').addEventListener('change',<?php function()<?php {<?php if (this.files &&<?php this.files[0])<?php {<?php const allowedTypes =<?php ['image/jpeg',<?php 'image/png',<?php 'image/jpg'];<?php if (!allowedTypes.includes(this.files[0].type))<?php {<?php Notiflix.Notify.failure('Formato de arquivo inválido!<?php Use apenas JPG ou PNG.');<?php this.value =<?php '';<?php return;<?php }<?php <?php if (this.files[0].size ><?php 5 *<?php 1024 *<?php 1024)<?php {<?php Notiflix.Notify.failure('Arquivo muito grande!<?php Tamanho máximo:<?php 5MB');<?php this.value =<?php '';<?php return;<?php }<?php <?php Notiflix.Loading.circle('Enviando banner...');<?php this.closest('form').submit();<?php }<?php });<?php <?php function openEditModal(bannerId,<?php bannerImg)<?php {<?php document.getElementById('editBannerId').value =<?php bannerId;<?php document.getElementById('currentImage').src =<?php bannerImg;<?php document.getElementById('editModal').classList.add('active');<?php document.body.style.overflow =<?php 'hidden';<?php }<?php <?php function closeEditModal()<?php {<?php document.getElementById('editModal').classList.remove('active');<?php document.body.style.overflow =<?php 'auto';<?php document.getElementById('editForm').reset();<?php document.getElementById('selectedFileName').style.display =<?php 'none';<?php }<?php <?php document.getElementById('editBannerInput').addEventListener('change',<?php function()<?php {<?php const fileNameDiv =<?php document.getElementById('selectedFileName');<?php if (this.files &&<?php this.files[0])<?php {<?php const allowedTypes =<?php ['image/jpeg',<?php 'image/png',<?php 'image/jpg'];<?php if (!allowedTypes.includes(this.files[0].type))<?php {<?php Notiflix.Notify.failure('Formato de arquivo inválido!<?php Use apenas JPG ou PNG.');<?php this.value =<?php '';<?php fileNameDiv.style.display =<?php 'none';<?php return;<?php }<?php <?php if (this.files[0].size ><?php 5 *<?php 1024 *<?php 1024)<?php {<?php Notiflix.Notify.failure('Arquivo muito grande!<?php Tamanho máximo:<?php 5MB');<?php this.value =<?php '';<?php fileNameDiv.style.display =<?php 'none';<?php return;<?php }<?php <?php fileNameDiv.textContent =<?php '✓<?php '<?php +<?php this.files[0].name;<?php fileNameDiv.style.display =<?php 'block';<?php }<?php else {<?php fileNameDiv.style.display =<?php 'none';<?php }<?php });<?php <?php document.getElementById('editModal').addEventListener('click',<?php function(e)<?php {<?php if (e.target ===<?php this)<?php {<?php closeEditModal();<?php }<?php });<?php <?php document.addEventListener('DOMContentLoaded',<?php ()<?php =><?php {<?php console.log('%c🖼️<?php Gestão de Banners carregada!',<?php 'color:<?php #22c55e;<?php font-size:<?php 16px;<?php font-weight:<?php bold;');<?php <?php if (window.innerWidth <=<?php 1024)<?php {<?php sidebar.classList.add('hidden');<?php }<?php <?php const containers =<?php document.querySelectorAll('.content-container');<?php containers.forEach((container,<?php index)<?php =><?php {<?php container.style.opacity =<?php '0';<?php container.style.transform =<?php 'translateY(30px)';<?php setTimeout(()<?php =><?php {<?php container.style.transition =<?php 'all 0.8s cubic-bezier(0.4,<?php 0,<?php 0.2,<?php 1)';<?php container.style.opacity =<?php '1';<?php container.style.transform =<?php 'translateY(0)';<?php },<?php 200 +<?php (index *<?php 150));<?php });<?php <?php const bannerCards =<?php document.querySelectorAll('.banner-card');<?php bannerCards.forEach((card,<?php index)<?php =><?php {<?php card.style.opacity =<?php '0';<?php card.style.transform =<?php 'translateY(20px)';<?php setTimeout(()<?php =><?php {<?php card.style.transition =<?php 'all 0.6s ease';<?php card.style.opacity =<?php '1';<?php card.style.transform =<?php 'translateY(0)';<?php },<?php 600 +<?php (index *<?php 100));<?php });<?php });<?php <?php document.documentElement.s