php <i class="fas fa-info-circle"></i><?php Percentual sobre as perdas dos usuários indicados <?php </p><?php </div><?php </div><?php </div><?php <?php <button type="submit"<?php name="salvar_config"<?php class="submit-button"><?php <i class="fas fa-save"></i><?php Salvar Configurações <?php </button><?php </form><?php </div><?php <?php </div><?php </main><?php <?php <script><?php //<?php Mobile menu toggle <?php const menuToggle =<?php document.getElementById('menuToggle');<?php const sidebar =<?php document.getElementById('sidebar');<?php const mainContent =<?php document.getElementById('mainContent');<?php const overlay =<?php document.getElementById('overlay');<?php <?php menuToggle.addEventListener('click',<?php ()<?php =><?php {<?php const isHidden =<?php sidebar.classList.contains('hidden');<?php <?php if (isHidden)<?php {<?php sidebar.classList.remove('hidden');<?php overlay.classList.add('active');<?php }<?php else {<?php sidebar.classList.add('hidden');<?php overlay.classList.add('active');<?php }<?php });<?php <?php overlay.addEventListener('click',<?php ()<?php =><?php {<?php sidebar.classList.add('hidden');<?php overlay.classList.remove('active');<?php });<?php <?php //<?php Close sidebar on window resize if it's mobile <?php window.addEventListener('resize',<?php ()<?php =><?php {<?php if (window.innerWidth <=<?php 1024)<?php {<?php sidebar.classList.add('hidden');<?php overlay.classList.remove('active');<?php }<?php else {<?php sidebar.classList.remove('hidden');<?php overlay.classList.remove('active');<?php }<?php });<?php <?php //<?php Enhanced hover effects for nav items <?php document.querySelectorAll('.nav-item').forEach(item =><?php {<?php item.addEventListener('mouseenter',<?php function()<?php {<?php this.style.transform =<?php 'translateX(8px)';<?php });<?php <?php item.addEventListener('mouseleave',<?php function()<?php {<?php if (!this.classList.contains('active'))<?php {<?php this.style.transform =<?php 'translateX(0)';<?php }<?php });<?php });<?php <?php //<?php File input enhancement <?php document.getElementById('logo-upload').addEventListener('change',<?php function(e)<?php {<?php const label =<?php document.getElementById('file-label');<?php const fileName =<?php e.target.files[0]?.name;<?php <?php if (fileName)<?php {<?php label.innerHTML =<?php `<?php <i class="fas fa-check-circle"></i><?php <span>${fileName}</span><?php `;<?php label.classList.add('has-file');<?php }<?php else {<?php label.innerHTML =<?php `<?php <i class="fas fa-cloud-upload-alt"></i><?php <span>Clique para enviar logo (JPG,<?php PNG)</span><?php `;<?php label.classList.remove('has-file');<?php }<?php });<?php <?php //<?php Input formatting for currency fields <?php function formatCurrency(input)<?php {<?php let value =<?php input.value.replace(/\D/g,<?php '');<?php if (value ===<?php '')<?php return;<?php value =<?php (value /<?php 100).toFixed(2)<?php +<?php '';<?php value =<?php value.replace(".",<?php ",");<?php value =<?php value.replace(/(\d)(\d{3}),/,<?php "$1.$2,");<?php input.value =<?php value;<?php }<?php <?php //<?php Input formatting for percentage fields <?php function formatPercentage(input)<?php {<?php let value =<?php input.value.replace(/[^\d,]/g,<?php '');<?php if (value.includes(','))<?php {<?php let parts =<?php value.split(',');<?php if (parts[1]<?php &&<?php parts[1].length ><?php 2)<?php {<?php parts[1]<?php =<?php parts[1].substring(0,<?php 2);<?php }<?php value =<?php parts.join(',');<?php }<?php input.value =<?php value;<?php }<?php <?php //<?php Apply currency formatting to financial inputs <?php document.querySelectorAll('input[name="deposito_min"],<?php input[name="saque_min"],<?php input[name="cpa_padrao"]').forEach(input =><?php {<?php input.addEventListener('input',<?php function()<?php {<?php formatCurrency(this);<?php });<?php });<?php <?php //<?php Apply percentage formatting to revshare input <?php document.querySelector('input[name="revshare_padrao"]').addEventListener('input',<?php function()<?php {<?php formatPercentage(this);<?php });<?php <?php //<?php Smooth scroll behavior <?php document.documentElement.style.scrollBehavior =<?php 'smooth';<?php <?php //<?php Initialize <?php document.addEventListener('DOMContentLoaded',<?php ()<?php =><?php {<?php console.log('%c⚙️<?php Configurações do Sistema carregadas!',<?php 'color:<?php #22c55e;<?php font-size:<?php 16px;<?php font-weight:<?php bold;');<?php <?php //<?php Check if mobile on load <?php if (window.innerWidth <=<?php 1024)<?php {<?php sidebar.classList.add('hidden');<?php }<?php <?php //<?php Animate form container on load <?php const formContainer =<?php document.querySelector('.form-container');<?php formContainer.style.opacity =<?php '0';<?php formContainer.style.transform =<?php 'translateY(20px)';<?php setTimeout(()<?php =><?php {<?php formContainer.style.transition =<?php 'all 0.6s ease';<?php formContainer.style.opacity =<?php '1';<?php formContainer.style.transform =<?php 'translateY(0)';<?php },<?php 300);<?php });<?php </script><?php 
