:<?php rgba(0,<?php 0,<?php 0,<?php 0.7);<?php z-index:<?php 1000;<?php opacity:<?php 0;<?php visibility:<?php hidden;<?php transition:<?php all 0.3s ease;<?php backdrop-filter:<?php blur(4px);<?php }<?php <?php .overlay.active {<?php opacity:<?php 1;<?php visibility:<?php visible;<?php }<?php </style><?php 
