 </section><?php <?php <!--<?php Stats Grid --><?php <section class="stats-grid"><?php <div class="mini-stat-card"><?php <div class="mini-stat-header"><?php <div class="mini-stat-icon"><?php <i class="fas fa-receipt"></i><?php </div><?php </div><?php <div class="mini-stat-value"><?php=<?php number_format($total_depositos,<?php 0,<?php ',',<?php '.')<?php ?></div><?php <div class="mini-stat-label">Total de Depósitos</div><?php </div><?php <?php <div class="mini-stat-card"><?php <div class="mini-stat-header"><?php <div class="mini-stat-icon"><?php <i class="fas fa-check-circle"></i><?php </div><?php </div><?php <div class="mini-stat-value"><?php=<?php number_format(count($depositos_aprovados),<?php 0,<?php ',',<?php '.')<?php ?></div><?php <div class="mini-stat-label">Depósitos Aprovados</div><?php </div><?php <?php <div class="mini-stat-card"><?php <div class="mini-stat-header"><?php <div class="mini-stat-icon warning"><?php <i class="fas fa-clock"></i><?php </div><?php </div><?php <div class="mini-stat-value"><?php=<?php number_format(count($depositos_pendentes),<?php 0,<?php ',',<?php '.')<?php ?></div><?php <div class="mini-stat-label">Depósitos Pendentes</div><?php </div><?php <?php <div class="mini-stat-card"><?php <div class="mini-stat-header"><?php <div class="mini-stat-icon"><?php <i class="fas fa-dollar-sign"></i><?php </div><?php </div><?php <div class="mini-stat-value">R$<?php <?php=<?php number_format($valor_total_aprovado,<?php 2,<?php ',',<?php '.')<?php ?></div><?php <div class="mini-stat-label">Valor Total Aprovado</div><?php </div><?php </section><?php <?php <!--<?php Filter Section --><?php <section class="filter-section"><?php <div class="filter-header"><?php <div class="filter-icon-container"><?php <i class="fas fa-filter"></i><?php </div><?php <h3 class="filter-title">Filtrar Depósitos</h3><?php </div><?php <?php <div class="filter-buttons"><?php <button class="filter-btn active"<?php onclick="filterDeposits('all')"><?php <i class="fas fa-list"></i><?php Todos os Depósitos <?php </button><?php <button class="filter-btn"<?php onclick="filterDeposits('PAID')"><?php <i class="fas fa-check-circle"></i><?php Aprovados <?php </button><?php <button class="filter-btn"<?php onclick="filterDeposits('PENDING')"><?php <i class="fas fa-clock"></i><?php Pendentes <?php </button><?php <button class="filter-btn"<?php onclick="filterDeposits('today')"><?php <i class="fas fa-calendar-day"></i><?php Hoje <?php </button><?php </div><?php </section><?php <?php <!--<?php Deposits Section --><?php <section><?php <?php if (empty($depositos)):<?php ?><?php <div class="empty-state"><?php <i class="fas fa-receipt"></i><?php <h3>Nenhum depósito encontrado</h3><?php <p>Não há<?php depósitos registrados no sistema ainda</p><?php </div><?php <?php else:<?php ?><?php <div class="deposits-grid"<?php id="depositsGrid"><?php <?php foreach ($depositos as $deposito):<?php ?><?php <div class="deposit-card"<?php data-status="<?php=<?php $deposito['status']<?php ?>"<?php data-date="<?php=<?php date('Y-m-d',<?php strtotime($deposito['updated_at']))<?php ?>"><?php <div class="deposit-header"><?php <div><?php <h3 class="deposit-user"><?php=<?php htmlspecialchars($deposito['nome'])<?php ?></h3><?php <?php if (!empty($deposito['transactionId'])):<?php ?><?php <div class="deposit-transaction"><?php ID:<?php <?php=<?php htmlspecialchars($deposito['transactionId'])<?php ?><?php </div><?php <?php endif;<?php ?><?php </div><?php <?php <div class="deposit-status <?php=<?php $deposito['status']<?php ==<?php 'PAID'<?php ?<?php 'approved'<?php :<?php 'pending'<?php ?>"><?php <div class="status-dot"></div><?php <span><?php=<?php $deposito['status']<?php ==<?php 'PAID'<?php ?<?php 'Aprovado'<?php :<?php 'Pendente'<?php ?></span><?php </div><?php </div><?php <?php <div class="deposit-value"><?php R$<?php <?php=<?php number_format($deposito['valor'],<?php 2,<?php ',',<?php '.')<?php ?><?php </div><?php <?php <div class="deposit-date"><?php <i class="fas fa-calendar"></i><?php <span><?php=<?php date('d/m/Y H:i',<?php strtotime($deposito['updated_at']))<?php ?></span><?php </div><?php </div><?php <?php endforeach;<?php ?><?php </div><?php <?php endif;<?php ?><?php </section><?php </div><?php </main><?php <?php <script><?php //<?php Mobile menu toggle with smooth animations <?php const menuToggle =<?php document.getElementById('menuToggle');<?php const sidebar =<?php document.getElementById('sidebar');<?php const mainContent =<?php document.getElementById('mainContent');<?php const overlay =<?php document.getElementById('overlay');<?php <?php menuToggle.addEventListener('click',<?php ()<?php =><?php {<?php const isHidden =<?php sidebar.classList.contains('hidden');<?php <?php if (isHidden)<?php {<?php sidebar.classList.remove('hidden');<?php overlay.classList.add('active');<?php }<?php else {<?php sidebar.classList.add('hidden');<?php overlay.classList.add('active');<?php }<?php });<?php <?php overlay.addEventListener('click',<?php ()<?php =><?php {<?php sidebar.classList.add('hidden');<?php overlay.classList.remove('active');<?php });<?php <?php //<?php Close sidebar on window resize if it's mobile <?php window.addEventListener('resize',<?php ()<?php =><?php {<?php if (window.innerWidth <=<?php 1024)<?php {<?php sidebar.classList.add('hidden');<?php overlay.classList.remove('active');<?php }<?php else {<?php sidebar.classList.remove('hidden');<?php overlay.classList.remove('active');<?php }<?php });<?php <?php //<?php Enhanced hover effects for nav items <?php document.querySelectorAll('.nav-item').forEach(item =><?php {<?php item.addEventListener('mouseenter',<?php function()<?php {<?php this.style.transform =<?php 'translateX(8px)';<?php });<?php <?php item.addEventListener('mouseleave',<?php function()<?php {<?php if (!this.classList.contains('active'))<?php {<?php this.style.transform =<?php 'translateX(0)';<?php }<?php });<?php });<?php <?php //<?php Filter functionality <?php function filterDeposits(filter)<?php {<?php const cards =<?php document.querySelectorAll('.deposit-card');<?php const buttons =<?php document.querySelectorAll('.filter-btn');<?php const today =<?php new Date().toISOString().split('T')[0];<?php <?php //<?php Update active button <?php buttons.forEach(btn =><?php btn.classList.remove('active'));<?php event.target.classList.add('active');<?php <?php cards.forEach(card =><?php {<?php let show =<?php false;<?php <?php switch(filter)<?php {<?php case 'all':<?php show =<?php true;<?php break;<?php case 'PAID':<?php show =<?php card.dataset.status ===<?php 'PAID';<?php break;<?php case 'PENDING':<?php show =<?php card.dataset.status !==<?php 'PAID';<?php break;<?php case 'today':<?php show =<?php card.dataset.date ===<?php today;<?php break;<?php }<?php <?php if (show)<?php {<?php card.style.display =<?php 'block';<?php card.style.opacity =<?php '0';<?php card.style.transform =<?php 'translateY(20px)';<?php setTimeout(()<?php =><?php {<?php card.style.transition =<?php 'all 0.3s ease';<?php card.style.opacity =<?php '1';<?php card.style.transform =<?php 'translateY(0)';<?php },<?php 50);<?php }<?php else {<?php card.style.opacity =<?php '0';<?php card.style.transform =<?php 'translateY(-20px)';<?php setTimeout(()<?php =><?php {<?php card.style.display =<?php 'none';<?php },<?php 300);<?php }<?php });<?php }<?php <?php //<?php Smooth scroll behavior <?php document.documentElement.style.scrollBehavior =<?php 'smooth';<?php <?php //<?php Initialize <?php document.addEventListener('DOMContentLoaded',<?php ()<?php =><?php {<?php console.log('%c💳<?php Gerenciamento de Depósitos carregado!',<?php 'color:<?php #22c55e;<?php font-size:<?php 16px;<?php font-weight:<?php bold;');<?php <?php //<?php Check if mobile on load <?php if (window.innerWidth <=<?php 1024)<?php {<?php sidebar.classList.add('hidden');<?php }<?php <?php //<?php Animate cards on load <?php const depositCards =<?php document.querySelectorAll('.deposit-card');<?php depositCards.forEach((card,<?php index)<?php =><?php {<?php card.style.opacity =<?php '0';<?php card.style.transform =<?php 'translateY(20px)';<?php setTimeout(()<?php =><?php {<?php card.style.transition =<?php 'all 0.6s ease';<?php card.style.opacity =<?php '1';<?php card.style.transform =<?php 'translateY(0)';<?php },<?php index *<?php 100);<?php });<?php <?php //<?php Animate stats cards <?php const statCards =<?php document.querySelectorAll('.mini-stat-card');<?php statCards.forEach((card,<?php index)<?php =><?php {<?php card.style.opacity =<?php '0';<?php card.style.transform =<?php 'translateY(20px)';<?php setTimeout(()<?php =><?php {<?php card.style.transition =<?php 'all 0.6s ease';<?php card.style.opacity =<?php '1';<?php card.style.transform =<?php 'translateY(0)';<?php },<?php index *<?php 150);<?php });<?php });<?php </script><?php 
