ner('DOMContentLoaded',<?php function()<?php {<?php //<?php Ref parameter handling <?php function getRefParam()<?php {<?php const urlParams =<?php new URLSearchParams(window.location.search);<?php return urlParams.get('ref');<?php }<?php <?php const refParam =<?php getRefParam();<?php if (refParam)<?php {<?php localStorage.setItem('ref',<?php refParam);<?php }<?php <?php const params =<?php new URLSearchParams(window.location.search);<?php let refValue =<?php params.get('ref');<?php <?php if (!refValue)<?php {<?php refValue =<?php localStorage.getItem('ref')<?php ||<?php '';<?php }<?php else {<?php localStorage.setItem('ref',<?php refValue);<?php }<?php <?php const refInput =<?php document.getElementById('ref');<?php if (refInput)<?php refInput.value =<?php refValue;<?php <?php //<?php Phone mask <?php const telefoneInput =<?php document.getElementById('telefone');<?php telefoneInput.addEventListener('input',<?php function(e)<?php {<?php let value =<?php e.target.value.replace(/\D/g,<?php '');<?php if (value.length ><?php 11)<?php value =<?php value.slice(0,<?php 11);<?php <?php let formatted =<?php '';<?php if (value.length ><?php 0)<?php {<?php formatted +=<?php '('<?php +<?php value.substring(0,<?php 2);<?php }<?php if (value.length >=<?php 3)<?php {<?php formatted +=<?php ')<?php '<?php +<?php value.substring(2,<?php 7);<?php }<?php if (value.length >=<?php 8)<?php {<?php formatted +=<?php '-'<?php +<?php value.substring(7);<?php }<?php e.target.value =<?php formatted;<?php });<?php <?php //<?php Form submission <?php const cadastroForm =<?php document.getElementById('cadastroForm');<?php const submitBtn =<?php document.getElementById('submitBtn');<?php <?php cadastroForm.addEventListener('submit',<?php function(e)<?php {<?php submitBtn.disabled =<?php true;<?php submitBtn.innerHTML =<?php '<i class="bi bi-arrow-repeat"<?php style="animation:<?php spin 1s linear infinite;"></i><?php Criando conta...';<?php cadastroForm.classList.add('loading');<?php });<?php <?php //<?php Add spin animation <?php const style =<?php document.createElement('style');<?php style.textContent =<?php `<?php @keyframes spin {<?php 0%<?php {<?php transform:<?php rotate(0deg);<?php }<?php 100%<?php {<?php transform:<?php rotate(360deg);<?php }<?php }<?php `;<?php document.head.appendChild(style);<?php <?php //<?php Focus enhancements <?php const inputs =<?php document.querySelectorAll('.form-input');<?php inputs.forEach(input =><?php {<?php input.addEventListener('focus',<?php function()<?php {<?php this.parentElement.style.transform =<?php 'translateY(-2px)';<?php });<?php <?php input.addEventListener('blur',<?php function()<?php {<?php this.parentElement.style.transform =<?php 'translateY(0)';<?php });<?php });<?php });<?php <?php //<?php Notiflix configuration <?php Notiflix.Notify.init({<?php width:<?php '300px',<?php position:<?php 'right-top',<?php distance:<?php '20px',<?php opacity:<?php 1,<?php borderRadius:<?php '12px',<?php timeout:<?php 4000,<?php success:<?php {<?php background:<?php '#22c55e',<?php textColor:<?php '#fff',<?php },<?php failure:<?php {<?php background:<?php '#ef4444',<?php textColor:<?php '#fff',<?php },<?php warning:<?php {<?php background:<?php '#f59e0b',<?php textColor:<?php '#fff',<?php }<?php });<?php <?php //<?php Show messages if any <?php <?php if (isset($_SESSION['message'])):<?php ?><?php Notiflix.Notify.<?php echo $_SESSION['message']['type'];<?php ?>('<?php echo $_SESSION['message']['text'];<?php ?>');<?php <?php unset($_SESSION['message']);<?php ?><?php <?php endif;<?php ?><?php <?php console.log('%c🎯<?php RaspaGreen -<?php Cadastro Split Screen',<?php 'color:<?php #22c55e;<?php font-size:<?php 16px;<?php font-weight:<?php bold;');<?php </script><?php 
