d=122';<?php <?php let orderId =<?php null;<?php let brushRadius =<?php 55;<?php let isDrawing =<?php false;<?php let scratchedPercentage =<?php 0;<?php let isScratchEnabled =<?php false;<?php <?php function ajustarCanvas()<?php {<?php const size =<?php container.clientWidth;<?php canvas.width =<?php size;<?php canvas.height =<?php size;<?php drawScratchImage();<?php }<?php <?php function resetCanvas()<?php {<?php if (canvas &&<?php canvas.parentNode)<?php canvas.parentNode.removeChild(canvas);<?php <?php const newCanvas =<?php document.createElement('canvas');<?php newCanvas.id =<?php 'scratch-canvas';<?php newCanvas.className =<?php canvas.className;<?php container.appendChild(newCanvas);<?php <?php canvas =<?php newCanvas;<?php ctx =<?php newCanvas.getContext('2d');<?php <?php ajustarCanvas();<?php addCanvasListeners();<?php }<?php <?php function addCanvasListeners()<?php {<?php canvas.replaceWith(canvas.cloneNode(true));<?php canvas =<?php document.getElementById('scratch-canvas');<?php ctx =<?php canvas.getContext('2d');<?php <?php canvas.addEventListener('mousedown',<?php handleStart);<?php canvas.addEventListener('mousemove',<?php handleMove);<?php canvas.addEventListener('mouseup',<?php handleEnd);<?php canvas.addEventListener('mouseleave',<?php handleEnd);<?php canvas.addEventListener('touchstart',<?php handleStart,<?php {passive:false});<?php canvas.addEventListener('touchmove',<?php handleMove,<?php {passive:false});<?php canvas.addEventListener('touchend',<?php handleEnd);<?php canvas.addEventListener('touchcancel',<?php handleEnd);<?php }<?php <?php window.addEventListener('resize',<?php ajustarCanvas);<?php scratchImage.onload =<?php ()<?php =><?php {<?php ajustarCanvas();<?php };<?php <?php function drawScratchImage()<?php {<?php ctx.clearRect(0,<?php 0,<?php canvas.width,<?php canvas.height);<?php ctx.globalCompositeOperation =<?php 'source-over';<?php ctx.drawImage(scratchImage,<?php 0,<?php 0,<?php canvas.width,<?php canvas.height);<?php }<?php <?php function scratch(x,<?php y)<?php {<?php if (!isScratchEnabled)<?php return;<?php ctx.globalCompositeOperation =<?php 'destination-out';<?php ctx.beginPath();<?php ctx.arc(x,<?php y,<?php brushRadius,<?php 0,<?php Math.PI *<?php 2);<?php ctx.fill();<?php }<?php <?php function getScratchedPercentage()<?php {<?php const imageData =<?php ctx.getImageData(0,<?php 0,<?php canvas.width,<?php canvas.height);<?php const pixels =<?php imageData.data;<?php let transparentPixels =<?php 0;<?php <?php for (let i =<?php 3;<?php i <?php pixels.length;<?php i +=<?php 4)<?php {<?php if (pixels[i]<?php ===<?php 0)<?php transparentPixels++;<?php }<?php return (transparentPixels /<?php (canvas.width *<?php canvas.height))<?php *<?php 100;<?php }<?php <?php function getMousePos(e)<?php {<?php const rect =<?php canvas.getBoundingClientRect();<?php if (e.touches)<?php {<?php return {<?php x:<?php e.touches[0].clientX -<?php rect.left,<?php y:<?php e.touches[0].clientY -<?php rect.top <?php };<?php }<?php else {<?php return {<?php x:<?php e.clientX -<?php rect.left,<?php y:<?php e.clientY -<?php rect.top <?php };<?php }<?php }<?php <?php function handleStart(e)<?php {<?php if (!isScratchEnabled)<?php return;<?php isDrawing =<?php true;<?php const pos =<?php getMousePos(e);<?php scratch(pos.x,<?php pos.y);<?php }<?php <?php function handleMove(e)<?php {<?php if (!isDrawing ||<?php !isScratchEnabled)<?php return;<?php const pos =<?php getMousePos(e);<?php scratch(pos.x,<?php pos.y);<?php scratchedPercentage =<?php getScratchedPercentage();<?php if (scratchedPercentage ><?php 75)<?php {<?php autoFinishScratch();<?php }<?php }<?php <?php function handleEnd()<?php {<?php isDrawing =<?php false;<?php }<?php <?php function buildCell(prize)<?php {<?php return `<?php <div class="prize-reveal"><?php <img src="${prize.icone}"<?php alt="${prize.nome}"<?php /><?php <span>${prize.valor ><?php 0 ?<?php 'R$<?php '<?php +<?php prize.valor.toLocaleString('pt-BR',<?php {<?php minimumFractionDigits:<?php 2 })<?php :<?php prize.nome}</span><?php </div><?php `;<?php }<?php <?php let fadeInterval =<?php null;<?php <?php async function autoFinishScratch()<?php {<?php isScratchEnabled =<?php false;<?php fadeInterval =<?php setInterval(()<?php =><?php {<?php ctx.globalCompositeOperation =<?php 'destination-out';<?php ctx.fillStyle =<?php 'rgba(0,0,0,0.1)';<?php ctx.fillRect(0,<?php 0,<?php canvas.width,<?php canvas.height);<?php },<?php 50);<?php <?php setTimeout(()<?php =><?php {<?php clearInterval(fadeInterval);<?php fadeInterval =<?php null;<?php ctx.clearRect(0,<?php 0,<?php canvas.width,<?php canvas.height);<?php },<?php 500);<?php <?php finishScratch();<?php }<?php <?php async function finishScratch()<?php {<?php resultMsg.innerHTML =<?php '<i class="bi bi-hourglass-split loading-pulse"></i><?php Verificando resultado...';<?php <?php const fd =<?php new FormData();<?php fd.append('order_id',<?php orderId);<?php const response =<?php await fetch('/raspadinhas/finish.php',<?php {<?php method:<?php 'POST',<?php body:<?php fd });<?php const json =<?php await response.json();<?php <?php if (!json.success)<?php {<?php Notiflix.Notify.failure('Erro ao finalizar.');<?php return;<?php }<?php <?php const jsConfetti =<?php new JSConfetti();<?php <?php if (json.valor ===<?php 0 ||<?php json.resultado ===<?php 'lose')<?php {<?php resultMsg.innerHTML =<?php `<?php <div style="color:<?php #ef4444;"><?php <i class="bi bi-emoji-frown"></i><?php NÃ£o foi dessa vez.<?php Tente novamente!<?php </div><?php `;<?php Notiflix.Notify.info('NÃ£o foi dessa vez.<?php ðŸ˜¢');<?php clearInterval(fadeInterval);<?php fadeInterval =<?php 0;<?php await atualizarSaldoUsuario();<?php }<?php else {<?php container.classList.add('win-animation');<?php resultMsg.innerHTML =<?php `<?php <div style="color:<?php #22c55e;"><?php <i class="bi bi-trophy-fill"></i><?php ðŸŽ‰<?php ParabÃ©ns!<?php VocÃª<?php ganhou R$<?php ${json.valor.toLocaleString('pt-BR',<?php {<?php minimumFractionDigits:<?php 2 })}!<?php </div><?php `;<?php Notiflix.Notify.success(`ðŸŽ‰<?php VocÃª<?php ganhou R$<?php ${json.valor.toLocaleString('pt-BR',<?php {<?php minimumFractionDigits:<?php 2 })}!`);<?php clearInterval(fadeInterval);<?php fadeInterval =<?php 0;<?php <?php jsConfetti.addConfetti({<?php emojis:<?php ['ðŸŽ‰',<?php 'âœ¨',<?php 'ðŸŽŠ',<?php 'ðŸ¥³',<?php 'ðŸ’°',<?php 'ðŸ€'],<?php emojiSize:<?php 20,<?php confettiNumber:<?php 300,<?php confettiRadius:<?php 6,<?php confettiColors:<?php ['#22c55e',<?php '#16a34a',<?php '#15803d',<?php '#166534',<?php '#14532d']<?php });<?php <?php await atualizarSaldoUsuario();<?php }<?php <?php btnBuy.style.opacity =<?php '1';<?php btnBuy.disabled =<?php false;<?php btnBuy.innerHTML =<?php '<i class="bi bi-arrow-clockwise"></i><?php Jogar Novamente';<?php }<?php <?php function reiniciarJogo()<?php {<?php if (fadeInterval)<?php {<?php clearInterval(fadeInterval);<?php fadeInterval =<?php null;<?php }<?php <?php container.classList.remove('win-animation');<?php prizesGrid.innerHTML =<?php '';<?php resultMsg.innerHTML =<?php '';<?php overlay.style.display =<?php 'flex';<?php orderId =<?php null;<?php scratchedPercentage =<?php 0;<?php isScratchEnabled =<?php false;<?php isDrawing =<?php false;<?php ctx.globalCompositeOperation =<?php 'source-over';<?php ajustarCanvas();<?php resetCanvas();<?php btnBuy.disabled =<?php false;<?php btnBuy.innerHTML =<?php '<i class="bi bi-credit-card"></i><?php Comprar e Raspar (R$<?php <?php=<?php number_format($cartela['valor'],<?php 2,<?php ',',<?php '.');<?php ?>)';<?php btnBuy.style.opacity =<?php '1';<?php }<?php <?php btnBuy.addEventListener('click',<?php async ()<?php =><?php {<?php if (btnBuy.innerHTML.includes('Jogar Novamente'))<?php {<?php reiniciarJogo();<?php setTimeout(()<?php =><?php btnBuy.click(),<?php 100);<?php return;<?php }<?php <?php btnBuy.disabled =<?php true;<?php btnBuy.innerHTML =<?php '<i class="bi bi-hourglass-split loading-pulse"></i><?php Gerando...';<?php resultMsg.innerHTML =<?php '';<?php prizesGrid.innerHTML =<?php '';<?php overlay.style.display =<?php 'none';<?php <?php const fd =<?php new FormData();<?php fd.append('raspadinha_id',<?php <?php=<?php $cartela['id'];<?php ?>);<?php const res =<?php await fetch('/raspadinhas/buy.php',<?php {<?php method:<?php 'POST',<?php body:<?php fd });<?php const json =<?php await res.json();<?php <?php if (!json.success)<?php {<?php Notiflix.Notify.failure(json.error);<?php btnBuy.disabled =<?php false;<?php btnBuy.innerHTML =<?php '<i class="bi bi-credit-card"></i><?php Comprar e Raspar';<?php overlay.style.display =<?php 'flex';<?php return;<?php }<?php <?php orderId =<?php json.order_id;<?php const premiosRes =<?php await fetch('/raspadinhas/prizes.php?ids='<?php +<?php json.grid.join(','));<?php const premios =<?php await premiosRes.json();<?php <?php prizesGrid.innerHTML =<?php premios.map(buildCell).join('');<?php drawScratchImage();<?php isScratchEnabled =<?php true;<?php btnBuy.style.opacity =<?php '0.6';<?php btnBuy.innerHTML =<?php '<i class="bi bi-hand-index"></i><?php Raspe a cartela!';<?php });<?php <?php //<?php Canvas event listeners <?php canvas.addEventListener('mousedown',<?php handleStart);<?php canvas.addEventListener('mousemove',<?php handleMove);<?php canvas.addEventListener('mouseup',<?php handleEnd);<?php canvas.addEventListener('mouseleave',<?php handleEnd);<?php canvas.addEventListener('touchstart',<?php handleStart);<?php canvas.addEventListener('touchmove',<?php handleMove);<?php canvas.addEventListener('touchend',<?php handleEnd);<?php canvas.addEventListener('touchcancel',<?php handleEnd);<?php <?php async function atualizarSaldoUsuario()<?php {<?php try {<?php const res =<?php await fetch('/api/get_saldo.php');<?php const json =<?php await res.json();<?php <?php if (json.success)<?php {<?php const saldoFormatado =<?php 'R$<?php '<?php +<?php json.saldo.toFixed(2).replace('.',<?php ',');<?php const el =<?php document.getElementById('headerSaldo');<?php if (el)<?php {<?php el.textContent =<?php saldoFormatado;<?php }<?php }<?php else {<?php console.warn('Erro ao buscar saldo:',<?php json.error);<?php }<?php }<?php catch (e)<?php {<?php console.error('Erro na requisiÃ§Ã£o de saldo:',<?php e);<?php }<?php }<?php <?php //<?php Initialize <?php document.addEventListener('DOMContentLoaded',<?php function()<?php {<?php console.log('%cðŸŽ®<?php Raspadinha carregada!',<?php 'color:<?php #22c55e;<?php font-size:<?php 16px;<?php font-weight:<?php bold;');<?php console.log(`Cartela:<?php ${<?php=<?php json_encode($cartela['nome']);<?php ?>}`);<?php });<?php </script><?php 
